import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { FirebaseAppProvider, useFirestoreDocData, useFirestore } from 'reactfire';
// components


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      maxWidth:345,

    },
    collegen:{  fontfamily:'Raleway',
},
    coursen:{  fontfamily:'Segoe UI',
},
    daten:{  fontfamily:'AmstelvarAlpha',
},
    fieldn:{  fontfamily:'AmstelvarAlpha',
},
    avatar: {
      backgroundColor: red[500],
    },
  }),
);

function Education() {
  var collegen ="";
  var coursen ="";
  var date ="";
  var fieldn ="";
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };
  const nameref = useFirestore()
  .collection('test')
  .doc('Conestoga');
  const firedata :any= useFirestoreDocData(nameref);
// /const about = firedata.data.somes;
try{
 collegen = firedata.data.CName;
 coursen = firedata.data.Course;
 date =  firedata.data.Date;
 fieldn =firedata.data.fieldn;
}catch(error){
  var collegen ="loading..";
  var coursen ="loading..";
  var date ="loading..";
  var fieldn ="loading..";
}
 return(
   <Card className={classes.root}>
        <CardHeader
          avatar={
            <Avatar aria-label="recipe" className={classes.avatar}>
              E
            </Avatar>
          }

          title="Education"
          subheader=""
        />

        <CardContent>
          <Typography color="textSecondary" component="p">

      <Box
        display="flex"
        alignItems="flex-start"
        p={1}
        m={1}
        bgcolor="background.paper"
        css={{ height: 100 }}
      >

        <Box flexGrow={1} className={classes.collegen}>  {collegen}</Box>

        <Box className={classes.coursen}>{coursen}</Box>
        <Box alignItems="center">  <div className={classes.daten}>{date}</div> </Box>
          <div className={classes.fieldn}>{fieldn}</div>
          </Box>

          </Typography>
        </CardContent>


      </Card>
 )
  //return <p>{about}</p>;
}



export default Education;
