import React, { FC, ReactElement } from 'react';
import { Helmet } from 'react-helmet';
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import 'firebase/firestore';
import Image from 'material-ui-image';
import { FirebaseAppProvider, useFirestoreDocData, useFirestore, SuspenseWithPerf } from 'reactfire';
// components
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
      root: {
        float:'right', },
   bullet: {
    display: 'inline-block',
  
   transform: 'scale(0.8)',
 },
 title: {
   fontSize: 14,
 },
 pos: {
   marginBottom: 12,
 },
        aboutme:{
          float: "right",
          width: "45%",
          padding: "40px",
          height: "475px",
           margin:"30px",
           background: "#fafafa",

        },
        quote:{

          float: "left",
          width: "45%",
          padding: "40px",
          height: "475px",
           margin:"30px",


        },


    }));
/*
function Aboutme() {
  const nameref = useFirestore()
  .collection('test')
  .doc('aboutme');
  const {status, data} = useFirestoreDocData(nameref);
  if (status === 'loading'){
    return <p>Wait</p>
  }
  return <p>{data.aboutme}</p>
}*/

function Aboutme(){
    const classes = useStyles();
  const nameref = useFirestore()
  .collection('test')
  .doc('aboutme');
  const {status, data} = useFirestoreDocData(nameref);
  if (status === 'loading'){
    return <p>Wait</p>
  }
  return (
    <>


    <div borderRadius="50%"  className={classes.quote}>
  {data.aboutme}
    </div>

    <Card className={classes.root}>
        <CardContent>
          <Typography className={classes.title} color="textSecondary" gutterBottom>

          </Typography>
          <Typography variant="h5" component="h2">
          {data.quote}
          </Typography>
          <Typography className={classes.pos} color="textSecondary">

          </Typography>
          <Typography variant="body2" component="p">

            <br />

          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small">Learn More</Button>
        </CardActions>
      </Card>
      </>
  )
}

export default Aboutme;
