import React, { FC, ReactElement } from 'react';
import { Helmet } from 'react-helmet';
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";

import 'firebase/firestore';
import Image from 'material-ui-image';
import { FirebaseAppProvider, useFirestoreDocData, useFirestore, SuspenseWithPerf } from 'reactfire';
// components
import PageTitle from "../components/PageTitle";
import Card from '@material-ui/core/Card';
// constants
import { APP_TITLE, ABOUTME} from '../utils/constants';


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            flex: 1,
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-between",
        },
        aboutme:{
          float: "right",
          width: "45%",
          padding: "40px",
          height: "475px",
           margin:"30px",
           background: "#fafafa",

        },
        quote:{
          float: "left",
          width: "45%",
          padding: "40px",
          height: "475px",
           margin:"30px",


        },


    }));

function Aboutme() {
  const nameref = useFirestore()
  .collection('test')
  .doc('aboutme');
  const {status, data} = useFirestoreDocData(nameref);
  if (status === 'loading'){
    return <p>Wait</p>
  }
  return <p>{data.aboutme}</p>
}

function Quote(){
  const nameref = useFirestore()
  .collection('test')
  .doc('aboutme');
  const {status, data} = useFirestoreDocData(nameref);
  if (status === 'loading'){
    return <p>Wait</p>
  }
  return (
    <>
    <div className={classes.root}>
    <PageTitle title={ABOUTME} />
    </div>

    <div borderRadius="50%"  className={classes.quote}>
    <Quote  />
    </div>

    <div borderRadius="50%"  className={classes.aboutme}>
    <Aboutme  />
    </div>
    </>
  )
}

export default Aboutme;
