import React, { FC, ReactElement } from 'react';
import { Helmet } from 'react-helmet';
import { MyForm } from "./MyForm";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import * as firebase from 'firebase';
import { FirebaseAppProvider, useFirestoreDocData, useFirestore, SuspenseWithPerf } from 'reactfire';
// components
import PageTitle from "../components/PageTitle";
import Box from '@material-ui/core/Box';

// constants
import { APP_TITLE, PAGE_TITLE_CODE } from '../utils/constants';
import { Button ,TextField} from '@material-ui/core';
// define css-in-js
const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            flex: 1,
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-between",
        },
        button: {
   margin: theme.spacing(1),
 },
    })
);

const CodeEditor: FC<{}> = (): ReactElement => {
    const classes = useStyles();
    const firebaseConfig = {
   apiKey: "AIzaSyByf-tlPYeu4KU8LcQB1yvOpkhv4C0ijig",
   authDomain: "parth-admin.firebaseapp.com",
   projectId: "parth-admin",
   storageBucket: "parth-admin.appspot.com",
   messagingSenderId: "247778178343",
   appId: "1:247778178343:web:c68d5d1b869c4b4acba502"
 };
    return (
        <>

            <Helmet>
                <title>{PAGE_TITLE_CODE} | {APP_TITLE}</title>
            </Helmet>
            <div className={classes.root}>
                <PageTitle title={PAGE_TITLE_CODE} />
            </div>
            <Box color="text.primary">
            <form className={classes.root} noValidate autoComplete="off">
            <div>
              <TextField required id="standard-required" label="Required" defaultValue="Hello World" />

              <TextField
                id="standard-password-input"
                label="Password"
                type="password"
                autoComplete="current-password"
              />

              <TextField
                id="standard-number"
                label="Number"
                type="number"
                InputLabelProps={{
                  shrink: true,
                }}
              />
              <TextField id="standard-search" label="Search field" type="search" />
              <TextField
                id="standard-helperText"
                label="Helper text"
                defaultValue="Default Value"
                helperText="Some important text"
              />
            </div>
            </form>
            <Button
              variant="contained"
              color="primary"
              size="large"
              className={classes.button}

            >
              Save
                </Button>

</Box>
            <div style={{ textAlign: "center" }}>
             <MyForm
               onSubmit={({ email, firstName, lastName }) => {

                 console.log(email, firstName, lastName);
               }}
             />
           </div>
        </>
    )
}

export default CodeEditor;
